import React from "react";
import { NavLink } from "react-router-dom";

function Header() {
  // Inline styles
  const headerStyle = {
    backgroundColor: "#4c2d48",
    color: "white",
    padding: "10px 20px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };

  const logoStyle = {
    fontSize: "24px",
    fontWeight: "bold",
  };

  const navStyle = {
    listStyle: "none",
    display: "flex",
    gap: "20px",
    margin: "0",
    padding: "0",
  };

  const navLinkStyle = {
    color: "white",
    textDecoration: "none",
  };

  const activeLinkStyle = {
    borderBottom: "2px solid #ffddc1",
  };

  const profileIconStyle = {
    width: "40px",
    height: "40px",
    borderRadius: "50%",
    backgroundColor: "#ffddc1",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontSize: "18px",
    color: "#4c2d48",
    cursor: "pointer",
  };

  // Component structure
  return React.createElement(
    "header",
    { style: headerStyle },
    React.createElement("div", { style: logoStyle }, "My App"),
    React.createElement(
      "ul",
      { style: navStyle },
      React.createElement(
        "li",
        null,
        React.createElement(
          NavLink,
          { to: "/", style: navLinkStyle, activeStyle: activeLinkStyle },
          "Home"
        )
      ),
      React.createElement(
        "li",
        null,
        React.createElement(
          NavLink,
          { to: "/about", style: navLinkStyle, activeStyle: activeLinkStyle },
          "About"
        )
      )
    ),
    React.createElement(
      "div",
      { style: profileIconStyle },
      "S" // Profile icon placeholder
    )
  );
}

export default Header;
