import React from "react";
import { NavLink } from "react-router-dom";

function Header() {
  const headerStyle = {
    backgroundColor: "#4c2d48",
    color: "white",
    padding: "10px 20px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };

  const logoStyle = {
    fontSize: "24px",
    fontWeight: "bold",
  };

  const navStyle = {
    listStyle: "none",
    display: "flex",
    gap: "20px",
    margin: "0",
    padding: "0",
  };

  const navLinkStyle = {
    color: "white",
    textDecoration: "none",
  };

  const profileIconStyle = {
    width: "40px",
    height: "40px",
    borderRadius: "50%",
    backgroundColor: "#ffddc1",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontSize: "18px",
    color: "#4c2d48",
    cursor: "pointer",
  };

  return React.createElement(
    "header",
    { style: headerStyle },
    React.createElement("div", { style: logoStyle }, "My App"),
    React.createElement(
      "ul",
      { style: navStyle },
      React.createElement(
        "li",
        null,
        React.createElement(
          NavLink,
          {
            to: "/homepage",
            style: ({ isActive }) => ({
              ...navLinkStyle,
              borderBottom: isActive ? "2px solid #ffddc1" : "none",
            }),
          },
          "Home"
        )
      ),
      React.createElement(
        "li",
        null,
        React.createElement(
          NavLink,
          {
            to: "/about-us",
            style: ({ isActive }) => ({
              ...navLinkStyle,
              borderBottom: isActive ? "2px solid #ffddc1" : "none",
            }),
          },
          "About"
        )
      )
    ),
    React.createElement("div", { style: profileIconStyle }, "S")
  );
}

export default Header;
