import React from "react";
import { NavLink } from "react-router-dom";

function Header() {
  const headerStyle = {
    backgroundColor: "#4c2d48",
    color: "white",
    padding: "10px 20px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };

  const logoStyle = {
    fontSize: "24px",
    fontWeight: "bold",
  };

  const navStyle = {
    listStyle: "none",
    display: "flex",
    gap: "20px",
    margin: "0",
    padding: "0",
  };

  const navLinkStyle = {
    color: "white",
    textDecoration: "none",
  };

  const activeLinkStyle = {
    borderBottom: "2px solid #ffddc1",
  };

  const profileIconStyle = {
    width: "40px",
    height: "40px",
    borderRadius: "50%",
    backgroundColor: "#ffddc1",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontSize: "18px",
    color: "#4c2d48",
    cursor: "pointer",
  };

  return React.createElement(
    "header",
    { style: headerStyle },
    React.createElement("div", { style: logoStyle }, "My App"),
    React.createElement(
      "ul",
      { style: navStyle },
      React.createElement(
        "li",
        null,
        React.createElement(
          NavLink,
          {
            to: "/",
            style: navLinkStyle,
            activeStyle: activeLinkStyle,
          },
          "Home"
        )
      ),
      React.createElement(
        "li",
        null,
        React.createElement(
          NavLink,
          {
            to: "/about-us",
            style: navLinkStyle,
            activeStyle: activeLinkStyle,
          },
          "About"
        )
      )
    ),
    React.createElement(
      "div",
      { style: profileIconStyle },
      "S" // Placeholder for the profile icon
    )
  );
}
document.head.appendChild(
  Object.assign(document.createElement("style"), {
    textContent: `
      header {
        background-color: #4c2d48;
        color: white;
        padding: 10px 20px;
        display: flex;
        justify-content: space-between;
        align-items: center;
      }
      .logo {
        font-size: 24px;
        font-weight: bold;
      }
      ul {
        list-style: none;
        display: flex;
        gap: 20px;
        margin: 0;
        padding: 0;
      }
      li a {
        color: white;
        text-decoration: none;
      }
      li a.active {
        border-bottom: 2px solid #ffddc1;
      }
      .profile-icon {
        width: 40px;
        height: 40px;
        border-radius: 50%;
        background-color: #ffddc1;
        display: flex;
        justify-content: center;
        align-items: center;
        font-size: 18px;
        color: #4c2d48;
        cursor: pointer;
      }
    `,
  })
);


export default Header;
