import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import logo from '../images/hera_logo.png'; // Use import for image
import '../index.css';

const Header = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if the user is authenticated (e.g., check for a token in local storage)
    const token = localStorage.getItem('access_token');
    console.log('Token:', token); // Debugging: Check if token is retrieved
    if (token) {
      setIsAuthenticated(true);
      console.log('User is authenticated'); // Debugging: Check if state is updated
    } else {
      console.log('User is not authenticated'); // Debugging: Check if state is not updated
    }
  }, []);

  const handleLogout = () => {
    // Remove the token from local storage
    localStorage.removeItem('access_token');
    setIsAuthenticated(false);
    navigate('/login_form'); // Redirect to login page
  };

  return (
    <header className="nav-bar-body">
      <div className="logo-nav-bar-container">
        <img src={logo} alt="Hera logo" className="logo" /> {/* Use imported image here */}

        <nav>
          <ul className="nav-links">
            <li><Link to="/homepage">Home</Link></li> 
            <li><Link to="/resources">Check-In</Link></li>
            <li><Link to="/about-us">About Us</Link></li> 
            <li><Link to="/self-assessment-quiz">Self Assessment</Link></li>
            {isAuthenticated ? (
              <li><button onClick={handleLogout} className="logout-button">Logout</button></li>
            ) : (
              <li><Link to="/login_form">Login</Link></li>
            )}
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
