    const teamMemberRole = document.createElement('p');
    teamMemberRole.textContent = 'Leading our team with vision and commitment to excellence.';
    teamMember.appendChild(teamMemberRole);
    teamSection.appendChild(teamMember);
    container.appendChild(teamSection);

    // Contact us section
    const contactSection = document.createElement('div');
    contactSection.classList.add('contact-section');
    const contactTitle = document.createElement('h2');
    contactTitle.textContent = 'Contact Us';
    contactSection.appendChild(contactTitle);

    const contactInfo = document.createElement('p');
    contactInfo.textContent = 'You can reach us at contact@company.com or call us at 123-456-7890.';
    contactSection.appendChild(contactInfo);
    container.appendChild(contactSection);

    // Add container to body
    document.body.appendChild(container);
}

